  <script type='text/javascript'>
  document.getElementById('form-footer').addEventListener('submit', async (e) => {
    e.preventDefault();

    const inputs = [
      document.querySelector('#fullname-input-footer'),
      document.querySelector('#email-input-footer'),
      document.querySelector('#message-input-footer'),
    ];
    const dropdowns = [
      document.querySelector('#speciality-input-footer')
    ];

    const isValidForm = formGuard(inputs, dropdowns);

    if (isValidForm) {
      openLoadingModal();
      const body = new FormData(e.target);
      console.log(body);
      
      const res = await fetch('', { method: 'POST', body: body });
      const response = await res.text();
      console.log(response);
      closeLoadingModal();
      if (response == 'success') {
        openSuccessModal();
      } else openFailModal();
    }
  });
  </script>

