<?php
  $from = get_field('mail_from', 'option'); 
  $to = get_field('mail_to', 'option');
  $subject = 'New Message!!!';

  $fullname = trim($_POST['fullname']);
  $email = trim($_POST['email']);
  $contact = trim($_POST['contact']);
  $phone = trim($_POST['phone']);
  $message = trim($_POST['message']);

  if (!empty($fullname) && !empty($email) && !empty($contact) && !empty($phone) && !empty($message)) {
    $headers = array(
      'From: ' . $from,
    );
    $msg = "fullname: $fullname \n email: $email \n contact: $contact \n phone: $phone \n message: $message";
       
    wp_mail($to, $subject, $msg, $headers);

    echo 'success';
    exit();
  };
?>

