<script>
const readMore = document.querySelector('#contact-page-read-more');
const showLess = document.querySelector('#contact-page-show-less');

const readMoreListener = () => {
  const hiddenText = document.querySelector('#contact-page-hidden-text');
  hiddenText.classList.add('show-text');

  readMore.style.display = 'none';
  showLess.style.display = 'block';

  readMore.removeEventListener('click', readMoreListener);
  showLess.addEventListener('click', showLessListener);
};

const showLessListener = () => {
  const hiddenText = document.querySelector('#contact-page-hidden-text');
  hiddenText.classList.remove('show-text');

  readMore.style.display = 'block';
  showLess.style.display = 'none';

  showLess.removeEventListener('click', showLessListener);
  readMore.addEventListener('click', readMoreListener);
};

readMore.addEventListener('click', readMoreListener);

// form

document.getElementById('form').addEventListener('submit', async (e) => {
  e.preventDefault();

  const inputs = [
    document.querySelector('#fullname-input'),
    document.querySelector('#email-input'),
    document.querySelector('#phone-input'),
    document.querySelector('#message-input'),
  ];
  const dropdowns = [
    document.querySelector('#contact-input'),
  ];

  const isValidForm = formGuard(inputs, dropdowns);

  if (isValidForm) {
    openLoadingModal();
    const body = new FormData(e.target);
    console.log(body);
    
    const res = await fetch('', { method: 'POST', body: body });
    const response = await res.text();
    console.log(response);
    closeLoadingModal();
    if (response == 'success') {
      openSuccessModal();
    } else openFailModal();
  }
});
</script>

